import{_ as s,c as a,o as e,a as l}from"./app.f7b0898d.js";const y=JSON.parse('{"title":"移动端适配","description":"","frontmatter":{},"headers":[{"level":2,"title":"适配要求","slug":"适配要求","link":"#适配要求","children":[]},{"level":2,"title":"适配设置","slug":"适配设置","link":"#适配设置","children":[]},{"level":2,"title":"快捷添加viewport","slug":"快捷添加viewport","link":"#快捷添加viewport","children":[]}],"relativePath":"css/css层叠样式表/移动端适配.md"}'),n={name:"css/css层叠样式表/移动端适配.md"},t=l(`<h1 id="移动端适配" tabindex="-1">移动端适配 <a class="header-anchor" href="#移动端适配" aria-hidden="true">#</a></h1><blockquote><p>移动端适配的目的就是使编辑的页面在不同尺寸的移动设备上，页面保持比较合理的尺寸比例，以及基本的样式，让用户在视觉上以及使用体验上不会有太大的偏差。</p></blockquote><h2 id="适配要求" tabindex="-1">适配要求 <a class="header-anchor" href="#适配要求" aria-hidden="true">#</a></h2><ul><li>网页宽度必须和浏览器保持一致</li><li>默认显示的缩放比例和PC端保持（缩放比例1.0）</li><li>不允许用户自行缩放网页</li></ul><h2 id="适配设置" tabindex="-1">适配设置 <a class="header-anchor" href="#适配设置" aria-hidden="true">#</a></h2><blockquote><p>如果任何设置都没有，默认走的就是viewport的默认设置</p></blockquote><p>去设置新的viewport设置,达到适配要求。</p><div class="language-html"><button title="Copy Code" class="copy"></button><span class="lang">html</span><pre class="shiki material-palenight"><code><span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">meta</span><span style="color:#89DDFF;"> </span><span style="color:#C792EA;">name</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">viewport</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;"> 设置视口的标签 在head里面并且应该紧接着编码设置</span></span>
<span class="line"><span style="color:#A6ACCD;">viewport的功能：</span></span>
<span class="line"><span style="color:#A6ACCD;">1. width 可以设置宽度 (device-width 当前设备的宽度)</span></span>
<span class="line"><span style="color:#A6ACCD;">2. height 可以设置高度</span></span>
<span class="line"><span style="color:#A6ACCD;">3. initial-scale 可以设置默认的缩放比例</span></span>
<span class="line"><span style="color:#A6ACCD;">4. user-scalable 可以设置是否允许用户自行缩放</span></span>
<span class="line"><span style="color:#A6ACCD;">5. maximum-scale 可以设置最大缩放比例</span></span>
<span class="line"><span style="color:#A6ACCD;">6. minimum-scale 可以设置最小缩放比例</span></span>
<span class="line"></span></code></pre></div><p>在<code>&lt;meta name=&quot;viewport&quot; content=&quot;&quot; &gt;</code>中 <code>content=&quot;&quot;</code>使用以上参数:</p><ol><li>width=device-width 宽度一致比例是1.0</li><li>initial-scale=1.0 宽度一致比例是1.0</li><li>user-scalable=no 不允许用户自行缩放 （yes，no 1,0）</li></ol><h2 id="快捷添加viewport" tabindex="-1">快捷添加viewport <a class="header-anchor" href="#快捷添加viewport" aria-hidden="true">#</a></h2><p>快捷方式：<code>meta:vp + tab</code></p><div class="language-html"><button title="Copy Code" class="copy"></button><span class="lang">html</span><pre class="shiki material-palenight"><code><span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">meta</span><span style="color:#89DDFF;"> </span><span style="color:#C792EA;">name</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">viewport</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;"> </span></span>
<span class="line"><span style="color:#C792EA;">content</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">width=device-width,initial-scale=1.0,user-scalable=0</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"></span></code></pre></div>`,13),o=[t];function p(c,i,r,d,h,D){return e(),a("div",null,o)}const F=s(n,[["render",p]]);export{y as __pageData,F as default};
